"use client";

import { useEffect, useState } from "react";
const aboutHero = "/assets/about-hero.jpg";

const AboutHero = () => {
    const [bgLoaded, setBgLoaded] = useState(false);
    const [contentVisible, setContentVisible] = useState(false);

    useEffect(() => {
        const img = new Image();
        img.src = aboutHero;
        img.onload = () => {
            setBgLoaded(true);
            setTimeout(() => setContentVisible(true), 300);
        };
    }, []);

    return (
        <section className="relative min-h-screen flex items-center justify-center overflow-hidden">
            {/* Background Image */}
            <div
                className={`absolute inset-0 transition-opacity duration-[2000ms] ${bgLoaded ? "opacity-100" : "opacity-0"
                    }`}
            >
                <img
                    src={aboutHero}
                    alt="Luxury spa sanctuary"
                    className="w-full h-full object-cover"
                />
            </div>

            {/* Dark Overlay */}
            <div className="absolute inset-0 bg-gradient-to-b from-black/50 via-black/40 to-black/60" />
            <div className="absolute inset-0 bg-gradient-to-r from-[hsl(38,45%,30%)]/15 via-transparent to-[hsl(38,45%,30%)]/15" />

            {/* Content */}
            <div className="relative z-10 container mx-auto px-6 text-center">
                {/* Decorative line */}
                <div
                    className={`w-px h-16 bg-gradient-to-b from-champagne/60 to-transparent mx-auto mb-8 transition-all duration-1000 ${contentVisible ? "opacity-100" : "opacity-0"
                        }`}
                />

                {/* Pill Badge */}
                <span
                    className={`inline-block font-body text-xs tracking-[0.3em] uppercase text-champagne-light mb-8 px-6 py-2 border border-champagne/30 rounded-full backdrop-blur-sm transition-all duration-700 ${contentVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-4"
                        }`}
                >
                    Our Philosophy
                </span>

                {/* Main Headline */}
                <h1
                    className={`font-heading text-4xl md:text-5xl lg:text-6xl xl:text-7xl text-white mb-6 transition-all duration-1000 delay-200 ${contentVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-6"
                        }`}
                >
                    Where Luxury Meets
                    <br />
                    <span className="italic text-champagne-light">Intention</span>
                </h1>

                {/* Subtext */}
                <p
                    className={`font-body text-lg md:text-xl text-white/85 max-w-xl mx-auto transition-all duration-1000 delay-400 ${contentVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-6"
                        }`}
                >
                    A thoughtfully designed wellness space focused on professionalism, comfort, and genuine care.
                </p>
            </div>

            {/* Scroll Indicator */}
            <div
                className={`absolute bottom-10 left-1/2 -translate-x-1/2 transition-all duration-1000 delay-1000 ${contentVisible ? "opacity-100" : "opacity-0"
                    }`}
            >
                <div className="w-px h-16 bg-gradient-to-b from-champagne/60 to-transparent animate-gentle-float" />
            </div>
        </section>
    );
};

export default AboutHero;

"use client";

import { useEffect, useState } from "react";
import Link from "next/link";

const spaleParisLogo = "/assets/spaleparis-logo.png";

const FranchiseHeader = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-700 ${isScrolled
        ? "bg-background/98 backdrop-blur-md shadow-card py-3 border-b border-border"
        : "bg-black/50 md:bg-transparent backdrop-blur-sm md:backdrop-blur-none py-4 md:py-6"
        }`}
    >
      <div className="container mx-auto px-6 flex items-center justify-between">
        <Link href="/" className="flex items-center">
          <img
            src={spaleParisLogo}
            alt="SpaleParis"
            className={`h-10 md:h-12 w-auto transition-all duration-500 ${isScrolled ? "brightness-0" : "brightness-0 invert"
              }`}
          />
        </Link>

        <Link
          href="/"
          className={`font-body text-xs tracking-luxury uppercase transition-colors duration-300 ${isScrolled
            ? "text-foreground/80 hover:text-champagne"
            : "text-white/90 hover:text-champagne"
            }`}
        >
          Back to Main Site
        </Link>
      </div>
    </header>
  );
};

export default FranchiseHeader;

"use client";

import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { ChevronDown } from "lucide-react";

const franchiseInterior = "/assets/franchise-interior.jpg";

const FranchiseHero = () => {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), 100);
    return () => clearTimeout(timer);
  }, []);

  const scrollToOpportunity = () => {
    document.getElementById("philosophy")?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <section className="relative min-h-screen flex items-center justify-center overflow-hidden">
      {/* Background Image with slow fade */}
      <div
        className={`absolute inset-0 transition-opacity duration-[2000ms] ${isLoaded ? "opacity-100" : "opacity-0"
          }`}
      >
        <img
          src={franchiseInterior}
          alt="Premium spa interior"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-b from-black/60 via-black/40 to-black/70" />
      </div>

      {/* Content */}
      <div className="relative z-10 container mx-auto px-6 text-center max-w-4xl">
        {/* Headline */}
        <h1
          className={`font-heading text-4xl md:text-5xl lg:text-6xl xl:text-7xl font-light text-white leading-tight mb-8 transition-all duration-1000 delay-500 ${isLoaded ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
            }`}
        >
          Partner With a Refined{" "}
          <span className="italic text-champagne-light">Wellness</span> Brand
        </h1>

        {/* Subtext */}
        <p
          className={`font-body text-lg md:text-xl text-white/80 font-light leading-relaxed mb-12 max-w-2xl mx-auto transition-all duration-1000 delay-700 ${isLoaded ? "opacity-100 translate-y-0" : "opacity-0 translate-y-6"
            }`}
        >
          We collaborate with like-minded partners to bring premium spa
          experiences to exceptional locations.
        </p>

        {/* CTA Button */}
        <div
          className={`transition-all duration-1000 delay-1000 ${isLoaded ? "opacity-100 translate-y-0" : "opacity-0 translate-y-4"
            }`}
        >
          <Button
            variant="heroOutline"
            size="hero"
            onClick={scrollToOpportunity}
            className="group hover:shadow-glow transition-shadow duration-500 h-auto py-4 whitespace-normal text-center max-w-[90vw]"
          >
            Explore Franchise Opportunity
            <ChevronDown className="ml-2 w-5 h-5 group-hover:translate-y-1 transition-transform duration-300" />
          </Button>
        </div>
      </div>

      {/* Scroll indicator */}
      <div
        className={`absolute bottom-12 left-1/2 -translate-x-1/2 transition-all duration-1000 delay-1500 ${isLoaded ? "opacity-100" : "opacity-0"
          }`}
      >
        <div className="w-px h-16 bg-gradient-to-b from-transparent via-champagne/50 to-transparent animate-pulse" />
      </div>
    </section>
  );
};

export default FranchiseHero;

import Script from "next/script";
import { toast } from "sonner";
import { useState } from "react";
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogDescription,
} from "@/components/ui/dialog";
import GiftPersonalization from "./GiftPersonalization";
import GiftDelivery from "./GiftDelivery";
import GiftPaymentSummary from "./GiftPaymentSummary";
import { X } from "lucide-react";

interface GiftCardModalProps {
    isOpen: boolean;
    onClose: () => void;
    selectedValue: number | null;
    selectedTotal: number | null;
    onProceedToPayment: (data: { recipientName: string; senderName: string; email: string; giftCode: string }) => void;
}

declare global {
    interface Window {
        Razorpay: any;
    }
}

const GiftCardModal = ({
    isOpen,
    onClose,
    selectedValue,
    selectedTotal,
    onProceedToPayment,
}: GiftCardModalProps) => {
    const [recipientName, setRecipientName] = useState("");
    const [senderName, setSenderName] = useState("");
    const [message, setMessage] = useState("");
    const [email, setEmail] = useState("");
    const [whatsappNumber, setWhatsappNumber] = useState("");
    const [sendViaWhatsapp, setSendViaWhatsapp] = useState(false);
    const [isProcessing, setIsProcessing] = useState(false);

    const formatCurrency = (amount: number) => {
        return new Intl.NumberFormat("en-IN", {
            style: "currency",
            currency: "INR",
            maximumFractionDigits: 0,
        }).format(amount);
    };

    const handlePayment = async () => {
        if (!selectedValue) return;
        setIsProcessing(true);

        try {
            // 1. Create Order
            const response = await fetch("/api/razorpay/order", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    amount: selectedValue * 100, // Amount in paise
                    currency: "INR",
                    notes: {
                        gift_value: selectedValue,
                        recipient_email: email,
                        sender_name: senderName,
                    },
                }),
            });

            if (!response.ok) {
                const error = await response.json();
                throw new Error(error.error || "Failed to create order");
            }

            const order = await response.json();

            // 2. Initialize Razorpay
            const options = {
                key: process.env.NEXT_PUBLIC_RAZORPAY_KEY_ID,
                amount: order.amount,
                currency: order.currency,
                name: "Spa Le Paris",
                description: `Gift Card - ${formatCurrency(selectedValue)}`,
                order_id: order.id,
                handler: async function (response: any) {
                    try {
                        const verifyRes = await fetch("/api/razorpay/verify", {
                            method: "POST",
                            headers: { "Content-Type": "application/json" },
                            body: JSON.stringify({
                                razorpay_order_id: response.razorpay_order_id,
                                razorpay_payment_id: response.razorpay_payment_id,
                                razorpay_signature: response.razorpay_signature,
                                amount: selectedValue,
                                recipient_email: email,
                                sender_name: senderName,
                            }),
                        });

                        const verifyData = await verifyRes.json();

                        if (verifyData.success) {
                            onProceedToPayment({
                                recipientName,
                                senderName,
                                email,
                                giftCode: verifyData.giftCode
                            });
                        } else {
                            toast.error("Payment verification failed. Please contact support.");
                        }
                    } catch (error) {
                        toast.error("Error verifying payment");
                        console.error(error);
                    }
                },
                prefill: {
                    name: senderName,
                    email: email,
                    contact: whatsappNumber,
                },
                theme: {
                    color: "#D4AF37", // Champagne Gold
                },
                modal: {
                    ondismiss: function () {
                        setIsProcessing(false);
                    }
                }
            };

            const rzp = new window.Razorpay(options);
            rzp.on("payment.failed", function (response: any) {
                toast.error(response.error.description || "Payment failed");
                setIsProcessing(false);
            });
            rzp.open();

        } catch (error: any) {
            console.error(error);
            toast.error(error.message || "Something went wrong");
            setIsProcessing(false);
        }
    };

    return (
        <>
            <Script
                id="razorpay-checkout-js"
                src="https://checkout.razorpay.com/v1/checkout.js"
            />
            <Dialog open={isOpen} onOpenChange={(open) => !isProcessing && onClose()}>
                <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto p-0 bg-background border-champagne/20">
                    <DialogHeader className="p-6 pb-0 sticky top-0 bg-background z-20 border-b border-border/50">
                        <div className="flex items-center justify-between">
                            <div>
                                <DialogTitle className="font-heading text-2xl text-foreground">
                                    Personalize Your Gift Card
                                </DialogTitle>
                                <DialogDescription className="font-body text-sm text-muted-foreground mt-1">
                                    Value: <span className="text-champagne font-medium">{selectedValue ? formatCurrency(selectedValue) : "—"}</span>
                                </DialogDescription>
                            </div>
                        </div>
                    </DialogHeader>

                    <div className="p-6 space-y-8">
                        <GiftPersonalization
                            recipientName={recipientName}
                            senderName={senderName}
                            message={message}
                            selectedValue={selectedValue}
                            onRecipientNameChange={setRecipientName}
                            onSenderNameChange={setSenderName}
                            onMessageChange={setMessage}
                        />

                        <GiftDelivery
                            email={email}
                            whatsappNumber={whatsappNumber}
                            sendViaWhatsapp={sendViaWhatsapp}
                            onEmailChange={setEmail}
                            onWhatsappChange={setWhatsappNumber}
                            onWhatsappToggle={setSendViaWhatsapp}
                        />

                        <GiftPaymentSummary
                            selectedValue={selectedValue}
                            selectedTotal={selectedTotal}
                            recipientName={recipientName}
                            email={email}
                            onProceedToPayment={handlePayment}
                        />
                    </div>
                </DialogContent>
            </Dialog>
        </>
    );
};

export default GiftCardModal;

"use client";

import { useState, useEffect, useRef } from "react";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Mail, MessageSquare, Zap } from "lucide-react";

interface GiftDeliveryProps {
  email: string;
  whatsappNumber: string;
  sendViaWhatsapp: boolean;
  onEmailChange: (email: string) => void;
  onWhatsappChange: (number: string) => void;
  onWhatsappToggle: (enabled: boolean) => void;
}

const GiftDelivery = ({
  email,
  whatsappNumber,
  sendViaWhatsapp,
  onEmailChange,
  onWhatsappChange,
  onWhatsappToggle,
}: GiftDeliveryProps) => {
  const [isVisible, setIsVisible] = useState(true);
  const sectionRef = useRef<HTMLElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) setIsVisible(true);
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div ref={sectionRef as any} className="pt-8 border-t border-border/50">
      <div className="text-center mb-8">
        <h2 className="font-heading text-2xl text-foreground mb-2">
          Delivery Method
        </h2>
        <p className="font-body text-sm text-muted-foreground">
          How would you like to receive your gift card?
        </p>
      </div>

      <div className="max-w-xl mx-auto">
        {/* Instant Delivery Banner */}
        <div
          className={`flex items-center gap-3 bg-champagne/10 text-champagne-dark rounded-xl px-5 py-4 mb-8 transition-all duration-700 delay-200 ${isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
            }`}
        >
          <Zap className="w-5 h-5 flex-shrink-0" strokeWidth={1.5} />
          <p className="font-body text-sm">
            The gift card will be delivered instantly with a unique redemption code.
          </p>
        </div>

        {/* Email Field */}
        <div
          className={`space-y-3 mb-8 transition-all duration-700 delay-300 ${isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
            }`}
        >
          <Label htmlFor="email" className="font-body text-sm text-foreground flex items-center gap-2">
            <Mail className="w-4 h-4 text-champagne" strokeWidth={1.5} />
            Recipient&apos;s Email Address
          </Label>
          <Input
            id="email"
            type="email"
            placeholder="recipient@email.com"
            value={email}
            onChange={(e) => onEmailChange(e.target.value)}
            className="bg-card border-border/50 focus:border-champagne/50 focus:ring-champagne/20 rounded-xl h-12"
          />
        </div>

        {/* WhatsApp Option */}
        <div
          className={`space-y-4 p-6 bg-card rounded-2xl border border-border/50 transition-all duration-700 delay-400 ${isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
            }`}
        >
          <div className="flex items-center space-x-3">
            <Checkbox
              id="whatsapp"
              checked={sendViaWhatsapp}
              onCheckedChange={(checked) => onWhatsappToggle(checked as boolean)}
              className="border-champagne/50 data-[state=checked]:bg-champagne data-[state=checked]:border-champagne"
            />
            <Label
              htmlFor="whatsapp"
              className="font-body text-sm text-foreground flex items-center gap-2 cursor-pointer"
            >
              <MessageSquare className="w-4 h-4 text-champagne" strokeWidth={1.5} />
              Also send via WhatsApp
            </Label>
          </div>

          {sendViaWhatsapp && (
            <div className="pt-2 animate-fade-in">
              <Input
                type="tel"
                placeholder="+91 98765 43210"
                value={whatsappNumber}
                onChange={(e) => onWhatsappChange(e.target.value)}
                className="bg-background border-border/50 focus:border-champagne/50 focus:ring-champagne/20 rounded-xl h-12"
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default GiftDelivery;

"use client";

import { useState, useEffect, useRef } from "react";
import { Button } from "@/components/ui/button";
import { Lock, Shield } from "lucide-react";

interface GiftPaymentSummaryProps {
  selectedValue: number | null;
  selectedTotal: number | null;
  recipientName: string;
  email: string;
  onProceedToPayment: () => void;
}

const GiftPaymentSummary = ({
  selectedValue,
  selectedTotal,
  recipientName,
  email,
  onProceedToPayment,
}: GiftPaymentSummaryProps) => {
  const [isVisible, setIsVisible] = useState(true);
  const sectionRef = useRef<HTMLElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) setIsVisible(true);
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const gstAmount = selectedValue ? selectedValue * 0.05 : 0;
  const isFormValid = selectedValue && recipientName.trim() && email.trim();

  return (
    <div ref={sectionRef as any} className="pt-8 border-t border-border/50">
      {/* Payment Summary Card */}
      <div
        className={`bg-card rounded-3xl p-8 border border-champagne/20 shadow-card transition-all duration-700 ${isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
          }`}
      >
        <h3 className="font-heading text-2xl text-foreground mb-6 text-center">
          Payment Summary
        </h3>

        {/* Summary Lines */}
        <div className="space-y-4 mb-8">
          <div className="flex justify-between items-center py-3 border-b border-border/50">
            <span className="font-body text-muted-foreground">Gift Card Value</span>
            <span className="font-heading text-lg text-foreground">
              {selectedValue ? formatCurrency(selectedValue) : "—"}
            </span>
          </div>

          <div className="flex justify-between items-center py-3 border-b border-border/50">
            <span className="font-body text-muted-foreground">GST (5%)</span>
            <span className="font-heading text-lg text-foreground">
              {selectedValue ? formatCurrency(gstAmount) : "—"}
            </span>
          </div>

          <div className="flex justify-between items-center py-4">
            <span className="font-body text-foreground font-medium">Total Payable</span>
            <span className="font-heading text-2xl text-champagne">
              {selectedTotal ? formatCurrency(selectedTotal) : "—"}
            </span>
          </div>
        </div>

        {/* CTA Button */}
        <Button
          variant="gold"
          size="lg"
          className="w-full shadow-soft hover:shadow-glow transition-all duration-500 h-14 text-base"
          disabled={!isFormValid}
          onClick={onProceedToPayment}
        >
          <Lock className="w-4 h-4 mr-2" strokeWidth={1.5} />
          Proceed to Secure Payment
        </Button>

        {/* Trust Badges */}
        <div className="flex items-center justify-center gap-2 mt-6 text-muted-foreground">
          <Shield className="w-4 h-4" strokeWidth={1.5} />
          <span className="font-body text-xs">Secure & encrypted payment</span>
        </div>

        {/* No Hidden Charges */}
        <p className="font-body text-xs text-center text-muted-foreground mt-4">
          No hidden charges. What you see is what you pay.
        </p>
      </div>
    </div>
  );
};

export default GiftPaymentSummary;

"use client";

import { useState, useEffect, useRef } from "react";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Gift } from "lucide-react";

interface GiftPersonalizationProps {
  recipientName: string;
  senderName: string;
  message: string;
  selectedValue: number | null;
  onRecipientNameChange: (name: string) => void;
  onSenderNameChange: (name: string) => void;
  onMessageChange: (message: string) => void;
}

const GiftPersonalization = ({
  recipientName,
  senderName,
  message,
  selectedValue,
  onRecipientNameChange,
  onSenderNameChange,
  onMessageChange,
}: GiftPersonalizationProps) => {
  const [isVisible, setIsVisible] = useState(true);
  const sectionRef = useRef<HTMLElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) setIsVisible(true);
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) observer.observe(sectionRef.current);
    return () => observer.disconnect();
  }, []);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat("en-IN", {
      style: "currency",
      currency: "INR",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div ref={sectionRef as any} className="space-y-12">

      <div className="grid lg:grid-cols-2 gap-12 max-w-5xl mx-auto items-start">
        {/* Form */}
        <div
          className={`space-y-6 transition-all duration-700 delay-300 ${isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
            }`}
        >
          <div className="space-y-2">
            <Label htmlFor="recipientName" className="font-body text-sm text-foreground">
              Recipient Name
            </Label>
            <Input
              id="recipientName"
              placeholder="Enter recipient&apos;s name"
              value={recipientName}
              onChange={(e) => onRecipientNameChange(e.target.value)}
              className="bg-card border-border/50 focus:border-champagne/50 focus:ring-champagne/20 rounded-xl h-12"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="senderName" className="font-body text-sm text-foreground">
              Your Name
            </Label>
            <Input
              id="senderName"
              placeholder="Enter your name"
              value={senderName}
              onChange={(e) => onSenderNameChange(e.target.value)}
              className="bg-card border-border/50 focus:border-champagne/50 focus:ring-champagne/20 rounded-xl h-12"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="message" className="font-body text-sm text-foreground">
              Personal Message{" "}
              <span className="text-muted-foreground">(optional)</span>
            </Label>
            <Textarea
              id="message"
              placeholder="Write a heartfelt message..."
              value={message}
              onChange={(e) => onMessageChange(e.target.value.slice(0, 200))}
              className="bg-card border-border/50 focus:border-champagne/50 focus:ring-champagne/20 rounded-xl min-h-[120px] resize-none"
            />
            <p className="font-body text-xs text-muted-foreground text-right">
              {message.length}/200 characters
            </p>
          </div>
        </div>

        {/* Live Preview */}
        <div
          className={`transition-all duration-700 delay-500 ${isVisible ? "opacity-100 translate-y-0" : "opacity-0 translate-y-8"
            }`}
        >
          <div className="bg-card rounded-3xl p-8 border border-champagne/20 shadow-card relative overflow-hidden">
            {/* Decorative Pattern */}
            <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-bl from-champagne/10 to-transparent rounded-bl-full" />
            <div className="absolute bottom-0 left-0 w-24 h-24 bg-gradient-to-tr from-champagne/5 to-transparent rounded-tr-full" />

            <div className="relative">
              {/* Header */}
              <div className="flex items-center justify-between mb-8">
                <span className="font-body text-xs tracking-luxury uppercase text-champagne">
                  Spa Le Paris
                </span>
                <Gift className="w-6 h-6 text-champagne" strokeWidth={1.5} />
              </div>

              {/* Gift Card Title */}
              <h3 className="font-heading text-2xl text-foreground mb-2">
                Gift Card
              </h3>

              {/* Value */}
              <p className="font-heading text-4xl text-champagne mb-6">
                {selectedValue ? formatCurrency(selectedValue) : "₹0"}
              </p>

              {/* Recipient */}
              <div className="mb-4">
                <p className="font-body text-xs text-muted-foreground uppercase tracking-wide mb-1">
                  For
                </p>
                <p className="font-heading text-xl text-foreground">
                  {recipientName || "Recipient Name"}
                </p>
              </div>

              {/* Message */}
              {message && (
                <div className="mb-4 p-4 bg-secondary/50 rounded-xl">
                  <p className="font-body text-sm text-muted-foreground italic leading-relaxed">
                    &quot;{message}&quot;
                  </p>
                </div>
              )}

              {/* Sender */}
              <div className="pt-4 border-t border-border/50">
                <p className="font-body text-xs text-muted-foreground">
                  With love, {senderName || "Your Name"}
                </p>
              </div>
            </div>
          </div>

          <p className="font-body text-xs text-muted-foreground text-center mt-4">
            Live preview of your gift card
          </p>
        </div>
      </div>
    </div>
  );
};

